const swalFire = (type, title, message) => {
    return Swal.fire({
        icon: type,
        title: title,
        text: message,
        confirmButtonClass: 'customSwalButton',
    });
}

const register = (event) => {
    event.preventDefault();

    const name = document.getElementById('name').value;
    const email = document.getElementById('email').value;
    let form = document.getElementById('register');

    axios.post('/users', {
        name,
        email
    })
    .then((res) => {

        if(res.data.staus === 'error'){
            swalFire('error', 'Oops...', res.data.message);
        }
        
        if(res.data.staus === 'success'){
            swalFire('success', 'Sucesso', res.data.message)
            form.reset();
        }
    })
    .catch((error) => {
        swalFire('error', 'Oops...', error);
    });
}